CREATE FUNCTION Get_Pri_Cost_Fifo (pcode    Ias_Itm_Mst.I_CODE%TYPE   ,
                                                         fd       Date       ,
                    	 			                             frst_fd  Date  ,
                                                         wcode    WAREHOUSE_DETAILS.W_CODE%TYPE Default Null  )Return Number Is
     pricost   Number :=0;
     opcost    Number :=0 ;
     wh        Number ;
  Begin
     IF fd =frst_fd Then
          IF  wcode Is Null  Then
                     Begin
                       Select Sum((((Nvl(p_qty,0)+Nvl(pf_qty,0))*Nvl(stk_cost,0))/Nvl(p_size,1))*in_out)/sum((Nvl(p_qty,0)+Nvl(pf_qty,0))*in_out)
                         Into opcost
                         From item_movement
                        Where I_Code = pcode
			                    And item_movement.doc_type =0 ;
                       Exception
                          When Others        Then
                           Return (0);
                      End;

 
          Else
                   Begin
                      Select Sum((((Nvl(p_qty,0)+Nvl(pf_qty,0))*Nvl(stk_cost,0))/Nvl(p_size,1))*in_out)/sum((Nvl(p_qty,0)+Nvl(pf_qty,0))*in_out)
                       Into opcost 
                       From Item_Movement
                      Where I_Code = pcode
		                    And item_movement.doc_type =0
                        And w_code=wcode;
                     Exception   When Others  Then
                       Return (0);
                  End;

          End If ;
  End IF  ;
  IF fd  <> frst_fd Then
        IF  wcode Is Null Then
                    Begin
                         Select Sum((((Nvl(p_qty,0)+Nvl(pf_qty,0))*Nvl(stk_cost,0))/Nvl(p_size,1))*in_out)/sum((Nvl(p_qty,0)+Nvl(pf_qty,0))*in_out)
                           Into pricost
                           From item_movement
                          Where I_Code = pcode
                            And i_date <fd ;
                       Exception
                          When Others        Then
                           Return (0);
                      End;
         Else
                    Begin
                         Select Sum((((Nvl(p_qty,0)+Nvl(pf_qty,0))*Nvl(stk_cost,0))/Nvl(p_size,1))*in_out)/sum((Nvl(p_qty,0)+Nvl(pf_qty,0))*in_out)
                           Into pricost 
                           From item_movement
                          Where I_Code = pcode
                            And i_date <fd
                            And w_code = wcode;
                       Exception
                          When Others        Then
                           Return (0);
                      End;

         End If;
      End if ;
       Return (NVl(pricost,0)+Nvl(opcost,0));
	Exception
	   When Others   Then
	     Return (SqlCode);
End Get_Pri_Cost_Fifo;
/
