	            End;
	         ElsIf P_Price_Type = 2 Then -- By W_Code 
	            Begin
	                Select Nvl(I_Price,0)
	                 Into V_Price
	                From Ias_Item_Price 
	                 Where I_Code  = P_Itm_Code
	                  And  Itm_Unt = P_Itm_Unt 
	                  And  Lev_No  = P_Lvl_No 
	                  And  W_Code  = P_W_Code ;                                 
	            Exception
	             When No_Data_Found Then
	                V_Price :=  0 ; 
	            End ;        
	         ElsIf P_Price_Type = 3 Then -- By Qty   
	            Begin
	                Select Nvl(I_Price,0)
	                 Into V_Price
	                From Ias_Item_Price 
	                 Where I_Code    = P_Itm_Code
	                  And  Itm_Unt   = P_Itm_Unt 
	                  And  Lev_No    = P_Lvl_No 
	                  And  From_Qty  = P_F_Qty
	                  And  To_Qty    = P_T_Qty ;
	            Exception
	             When No_Data_Found Then
	                V_Price :=  0 ; 
	            End ; 
	         ElsIf P_Price_Type = 4 Then -- By W_Code + Qty   
	            Begin
	                Select Nvl(I_Price,0)
	                 Into V_Price
	                From Ias_Item_Price 
	                 Where I_Code    = P_Itm_Code
	                  And  Itm_Unt   = P_Itm_Unt 
	                  And  Lev_No    = P_Lvl_No 
	                  And  W_Code    = P_W_Code                  
	                  And  From_Qty  = P_F_Qty
	                  And  To_Qty    = P_T_Qty ;
	            Exception
	             When No_Data_Found Then
	                V_Price :=  0 ; 
	            End ; 
