	                  And  Brn_No    = P_Brn_No ;
	            Exception
	             When No_Data_Found Then
	                V_Price :=  0 ; 
	            End ;                                      
	         End If ;
	          

	      End If ;
	    Return (Nvl(V_Price,0));
	  Exception
	    When Others Then
	      Return (0);
	 End Get_Itm_Price ;

