          Where M.ac_code_dtl Is Null
            and Exists ( Select 1 From Customer where c_a_code=M.a_code and RowNum<=1)
            and nvl(M.out_post,0) = Decode(P_Doc_Post,0,0,1,1,nvl(M.out_post,0))
