-- Trigger 3: IAS_DECR_AVLQTY_STRGE_Tgr
--------------------------------------------------------------
CREATE TRIGGER IAS_DECR_AVLQTY_STRGE_Tgr
BEFORE DELETE ON  ITEM_MOVEMENT   FOR EACH ROW
BEGIN
--## For Incoming
    If :OLD.IN_OUT = 1 Then
            Update Ias_Itm_Wcode Set Avl_Qty = nvl(Avl_Qty,0) - ((nvl(:OLD.I_qty,0)+ nvl(:OLD.FREE_Qty,0))*:OLD.P_SIZE)
             Where I_Code  = :Old.I_Code
             --And Itm_Unt = :Old.Itm_Unt
               And w_code  = :Old.W_CODE;

             Begin
               Update Ias_Itm_Sales_Age 
                Set    Doc_Ser_Inc    = Null ,
                       Doc_Seq_Inc    = Null ,
                       First_Inc_Date = Null
                 Where I_Code         = :Old.I_Code
                   And Itm_Unt        = :Old.Itm_Unt
                   And W_Code         = :Old.W_Code
                   And Brn_Year       = :Old.Brn_Year
                   And Doc_Ser_Inc    = :Old.Doc_Ser
                   And Doc_Seq_Inc    = :Old.Doc_Sequence ;
             Exception
               When Others Then
                Null ;
             End ;

