-- Trigger 4: IAS_INCR_AVLQTY_STRGE_Tgr
--------------------------------------------------------------
CREATE TRIGGER IAS_INCR_AVLQTY_STRGE_Tgr
		AFTER INSERT ON  ITEM_MOVEMENT REFERENCING NEW AS NEW OLD AS OLD
		FOR EACH ROW
DECLARE
		 V_AVLQTY  NUMBER;
		 V_Cnt     Number := 0 ;
		Begin
		 Select Ias_Itm_Pkg.Get_grand_Avlqty (:NEW.I_CODE,:NEW.W_CODE)
		   Into V_AVLQTY
		   From Dual;
		 
		--## For Incoming
		 If :NEW.IN_OUT = 1 Then
		            
		            Update Ias_Itm_Wcode Set avl_qty = nvl(V_AVLQTY,0) + ((nvl(:NEW.I_QTY,0)+ nvl(:NEW.FREE_QTY,0))*:NEW.P_SIZE)
		             Where I_Code  = :NEW.I_Code
		             --And Itm_Unt = :NEW.Itm_Unt
		               And W_Code  = :NEW.W_Code;
		         If Sql%NotFound Then
		            Insert Into Ias_Itm_Wcode(  I_CODE, 
	                                          Itm_Unt, 
	                                          P_SIZE,
	                                          W_CODE, 
	                                          AVL_QTY,
	                                          Whg_Code,
                                            Primary_Cost,
                                            I_Cwtavg,
                                            Inactive)
	                                  VALUES( :NEW.I_CODE, 
	                                          Ias_Itm_Pkg.Get_Icode_Min_Unit (P_I_Code => :NEW.I_CODE), 
	                                          1,
	                                          :NEW.W_CODE, 
	                                          ((nvl(:NEW.I_QTY,0)+ nvl(:NEW.FREE_QTY,0))*:NEW.P_SIZE),
	                                          Ias_Wcode_Pkg.Get_Whg_Code  (:New.W_Code),
                                            0,
                                            Ias_Itm_Pkg.Get_Grand_Wtavg( P_Wtavg_Type => (select Wtavg_Type from Ias_Para_Inv Where RowNum<=1),
                                                                         P_Icode      => :NEW.I_CODE ,
                                                                         P_Wcode      => :NEW.W_CODE ),
                                            0);
		         End If;

		              
		         If  Nvl(V_AVLQTY,0) = 0 And :New.Doc_Type <> 0 Then
		             Begin
		               Update Ias_Itm_Sales_Age 
		                Set    Doc_Ser_Inc    = :New.Doc_Ser      ,
		                       Doc_Seq_Inc    = :New.Doc_Sequence ,
		                       First_Inc_Date = :New.I_Date
		                 Where I_Code         = :New.I_Code
		                   And Itm_Unt        = :New.Itm_Unt
		                   And W_Code         = :New.W_Code
		                   And Brn_Year       = :New.Brn_Year
		                   And Doc_Ser_Out Is Not Null
		                   And Doc_Seq_Out Is Not Null
		                   And Doc_Ser_Inc Is Null
		                   And Doc_Seq_Inc Is Null ; 
		             Exception
		               When Others Then
		                Null ;
		             End ;

