-- Trigger 5: IAS_VD_INSTALLMNT_Tgr
--------------------------------------------------------------
CREATE TRIGGER IAS_VD_INSTALLMNT_Tgr
BEFORE DELETE OR INSERT OR UPDATE
OF A_CY
  ,AC_AMT
  ,AC_AMTF
  ,BILL_NO
  ,BILL_SER
  ,I_NO
ON VOUCHER_DETAIL 
REFERENCING NEW AS NEW OLD AS OLD
FOR EACH ROW
BEGIN

If DELETING Then --(((1)

If :OLD.VOUCHER_TYPE = 1 AND :OLD.BILL_SER Is Not Null AND :OLD.I_NO Is Not Null AND :OLD.AC_CODE_DTL Is Not Null  AND :OLD.AC_DTL_TYP=3 Then --(2)

		 UPDATE INSTALLMENT SET PAID_AMT=NVL(PAID_AMT,0)- DECODE(NVL(:OLD.AC_AMTF,0),0,ABS(:OLD.AC_AMT),ABS(:OLD.AC_AMTF))
		  WHERE C_CODE=:OLD.AC_CODE_DTL
		    AND A_CY  =:OLD.A_CY
			  AND BILL_SER =:OLD.BILL_SER
		    AND I_NO =:OLD.I_NO;
  
  ELSIf :OLD.VOUCHER_TYPE = 2 AND :OLD.BILL_SER Is Not Null AND :OLD.I_NO Is Not Null AND :OLD.AC_CODE_DTL Is Not Null  AND :OLD.AC_DTL_TYP=4 Then --(2)
  
  	 UPDATE IAS_INSTALLMENT_PI SET PAID_AMT=NVL(PAID_AMT,0)- DECODE(NVL(:OLD.AC_AMTF,0),0,ABS(:OLD.AC_AMT),ABS(:OLD.AC_AMTF))
		  WHERE V_CODE=:OLD.AC_CODE_DTL
		    AND A_CY  =:OLD.A_CY
			  AND BILL_SER =:OLD.BILL_SER
		    AND I_NO =:OLD.I_NO;
  
	End If;

ElsIf INSERTING Then --(1)

If :NEW.VOUCHER_TYPE = 1 AND :NEW.BILL_SER Is Not Null AND :NEW.I_NO Is Not Null AND :NEW.AC_CODE_DTL Is Not Null  AND :NEW.AC_DTL_TYP=3 Then --(3)

		 UPDATE INSTALLMENT SET PAID_AMT=NVL(PAID_AMT,0) + DECODE(NVL(:NEW.AC_AMTF,0),0,ABS(:NEW.AC_AMT),ABS(:NEW.AC_AMTF))
		  WHERE C_CODE=:NEW.AC_CODE_DTL
		    AND A_CY  =:NEW.A_CY
			  AND BILL_SER =:NEW.BILL_SER
		    AND I_NO =:NEW.I_NO;
  
  ELSIf :NEW.VOUCHER_TYPE = 2 AND :NEW.BILL_SER Is Not Null AND :NEW.I_NO Is Not Null AND :NEW.AC_CODE_DTL Is Not Null  AND :NEW.AC_DTL_TYP=4 Then --(3)	

		 UPDATE IAS_INSTALLMENT_PI SET PAID_AMT=NVL(PAID_AMT,0) + DECODE(NVL(:NEW.AC_AMTF,0),0,ABS(:NEW.AC_AMT),ABS(:NEW.AC_AMTF))
		  WHERE V_CODE=:NEW.AC_CODE_DTL
		    AND A_CY  =:NEW.A_CY
			  AND BILL_SER =:NEW.BILL_SER
		    AND I_NO =:NEW.I_NO;
  
	End If;--(3)

ElsIf UPDATING Then --(1)

If :OLD.VOUCHER_TYPE = 1 AND :OLD.BILL_SER Is Not Null AND :OLD.I_NO Is Not Null AND :OLD.AC_CODE_DTL Is Not Null  AND :OLD.AC_DTL_TYP=3 Then 

	 UPDATE INSTALLMENT SET PAID_AMT=NVL(PAID_AMT,0)- DECODE(nvl(:OLD.AC_AMTF,0),0,ABS(nvl(:OLD.AC_AMT,0)),ABS(nvl(:OLD.AC_AMTF,0)))
		  WHERE C_CODE =:OLD.AC_CODE_DTL
		    AND A_CY   =:OLD.A_CY
			  AND BILL_SER =:OLD.BILL_SER
		    AND I_NO   =:OLD.I_NO;

	End If;

If :NEW.VOUCHER_TYPE = 1 AND :NEW.BILL_SER Is Not Null AND :NEW.I_NO Is Not Null AND :NEW.AC_CODE_DTL Is Not Null  AND :NEW.AC_DTL_TYP=3 Then
	 UPDATE INSTALLMENT SET PAID_AMT=NVL(PAID_AMT,0) + DECODE(nvl(:NEW.AC_AMTF,0),0,ABS(nvl(:NEW.AC_AMT,0)),ABS(nvl(:NEW.AC_AMTF,0)))
		  WHERE C_CODE=:NEW.AC_CODE_DTL
		    AND A_CY  =:NEW.A_CY
			  AND BILL_SER =:NEW.BILL_SER
	      AND I_NO =:NEW.I_NO;

	End If;


If :OLD.VOUCHER_TYPE = 2 AND :OLD.BILL_SER Is Not Null AND :OLD.I_NO Is Not Null AND :OLD.AC_CODE_DTL Is Not Null  AND :OLD.AC_DTL_TYP=4 Then 

	 UPDATE IAS_INSTALLMENT_PI SET PAID_AMT=NVL(PAID_AMT,0)- DECODE (nvl(:OLD.AC_AMTF,0),0,ABS(nvl(:OLD.AC_AMT,0)),ABS(nvl(:OLD.AC_AMTF,0)))
		  WHERE V_CODE =:OLD.AC_CODE_DTL
		    AND A_CY   =:OLD.A_CY
		  	AND BILL_SER =:OLD.BILL_SER
		    AND I_NO   =:OLD.I_NO;

	End If;

If :NEW.VOUCHER_TYPE = 2 AND :NEW.BILL_SER Is Not Null AND :NEW.I_NO Is Not Null AND :NEW.AC_CODE_DTL Is Not Null  AND :NEW.AC_DTL_TYP=4 Then

	 UPDATE IAS_INSTALLMENT_PI SET PAID_AMT=NVL(PAID_AMT,0) + DECODE(nvl(:NEW.AC_AMTF,0),0,ABS(nvl(:NEW.AC_AMT,0)),ABS(nvl(:NEW.AC_AMTF,0)))
		  WHERE V_CODE=:NEW.AC_CODE_DTL
		    AND A_CY  =:NEW.A_CY
			  AND BILL_SER =:NEW.BILL_SER
		    AND I_NO =:NEW.I_NO;

	End If;


End If;--(1)

 EXCEPTION WHEN OTHERS THEN
   Raise_Application_Error(-20303,' Error When Installment Trans. '||SqlErrm);
END;

