       IF INSERTING AND :NEW.LVL_NO = 1 AND :NEW.APPRVD = 0 THEN
              FOR I IN DVC_TOKEN_CRSR LOOP
                     IF :NEW.DOC_TYP IS NOT NULL THEN
                            YS_MOBILE_APPRVD_PKG.NTFCATION_APPRVD_MOBILE(I.DVC_TOKEN,
                                                     YS_GEN_PKG.GET_DOCTYP_NM (P_DOC_TYP => :NEW.DOC_TYP, P_LNG_NO => 2),
