                                                     305,
                                                     :NEW.DOC_SER,
                                                     :NEW.DOC_TYP,
                                                     NULL,
                                                     I.DVS_TYP);

                    END IF;
              END LOOP;
       END IF;
EXCEPTION WHEN OTHERS THEN
   NULL;  
