				ON GLS_ADVNC_EXPNS_RVNU_MST FOR EACH ROW
			BEGIN
			     
				 IF :NEW.DOC_M_SQ IS NULL THEN    
				    :NEW.DOC_M_SQ  := IAS_DOC_SEQ_GL.Nextval; 
				 END IF;
			
				                      
